// ðŸ“§ EMAIL ORCHESTRATOR - Maneja campaÃ±as de email
const payload = $input.item.json;

console.log('ðŸ“§ === EMAIL ORCHESTRATOR v2.0 ===');
console.log('ðŸ“¥ Payload recibido');

// Extraer datos del lead
const leadData = payload.data?.lead || {};
const isFromConversion = payload.data?.isFromConversion || false;
const enrichmentData = payload.enrichment || null;

// ValidaciÃ³n bÃ¡sica
if (!leadData.id || !leadData.email) {
  return {
    json: {
      success: false,
      error: 'Missing lead ID or email for email orchestration',
      leadData: leadData
    }
  };
}

console.log(`ðŸ“§ Orquestando email para lead: ${leadData.id}`);
console.log(`ðŸ“§ Email: ${leadData.email}`);
console.log(`ðŸŽ¯ From conversion: ${isFromConversion}`);

const { v4: uuidv4 } = require('uuid');
const now = new Date().toISOString();

// ðŸŽ¯ DETERMINAR SECUENCIA DE EMAIL BASADA EN LEAD TYPE Y SCORE
let emailSequence = {
  campaignName: 'default_sequence',
  emailNumber: 1,
  subject: 'Bienvenido - Tu guÃ­a estÃ¡ lista',
  template: 'welcome_default',
  scheduledFor: new Date(Date.now() + (5 * 60 * 1000)).toISOString(), // 5 minutos
  priority: 'normal'
};

// 1. SECUENCIA PARA FREE GUIDE
if (leadData.leadType === 'free_guide') {
  emailSequence = {
    campaignName: `${leadData.source}_${leadData.leadType}`,
    emailNumber: 1,
    subject: 'Tu guÃ­a de transiciÃ³n a comida natural',
    template: 'free_guide_welcome',
    scheduledFor: new Date(Date.now() + (2 * 60 * 1000)).toISOString(), // 2 minutos
    priority: 'high',
    sequence: [
      { day: 0, subject: 'Tu guÃ­a de transiciÃ³n a comida natural', template: 'free_guide_welcome' },
      { day: 1, subject: 'Â¿Ya empezaste con las recetas?', template: 'free_guide_day1' },
      { day: 3, subject: 'Historias de Ã©xito de otros dueÃ±os', template: 'free_guide_day3' },
      { day: 7, subject: 'Oferta especial: Consulta veterinaria', template: 'free_guide_day7' }
    ]
  };
}

// 2. SECUENCIA PARA CONVERSIONES
if (isFromConversion) {
  emailSequence.priority = 'urgent';
  emailSequence.scheduledFor = new Date(Date.now() + (1 * 60 * 1000)).toISOString(); // 1 minuto
  emailSequence.campaignName = `conversion_${leadData.source}`;
  emailSequence.subject = 'Â¡Gracias por tu interÃ©s! Tu informaciÃ³n estÃ¡ lista';
  emailSequence.template = 'conversion_immediate';
}

// 3. AJUSTAR POR HOT LEAD
if (leadData.isHot || (enrichmentData && enrichmentData.enrichedScore > 70)) {
  emailSequence.priority = 'urgent';
  emailSequence.scheduledFor = new Date(Date.now() + (30 * 1000)).toISOString(); // 30 seconds
  emailSequence.subject = 'ðŸ”¥ ' + emailSequence.subject;
  emailSequence.template = 'hot_lead_' + emailSequence.template;
}

// 4. AJUSTAR POR GEOGRAFÃA
if (leadData.country === 'Uruguay') {
  emailSequence.campaignName += '_uy';
  emailSequence.subject += ' - EnvÃ­o gratis en Uruguay';
}

// ðŸŽ¯ PREPARAR EMAIL CAMPAIGN DATA
const emailCampaignData = {
  id: uuidv4(),
  businessId: leadData.businessId,
  leadId: leadData.id,
  contactId: null,
  campaignName: emailSequence.campaignName,
  emailNumber: emailSequence.emailNumber,
  subject: emailSequence.subject,
  template: emailSequence.template,
  status: 'scheduled',
  priority: emailSequence.priority,
  scheduledFor: emailSequence.scheduledFor,
  sentAt: null,
  deliveredAt: null,
  openedAt: null,
  clickedAt: null,
  // Datos del lead para personalizaciÃ³n
  recipientData: {
    email: leadData.email,
    name: leadData.name || 'Amigo de las mascotas',
    leadType: leadData.leadType,
    source: leadData.source,
    country: leadData.country,
    deviceType: leadData.deviceType,
    isHot: leadData.isHot,
    score: enrichmentData ? enrichmentData.enrichedScore : leadData.score
  },
  // Attribution para tracking
  attribution: {
    source: leadData.source,
    medium: leadData.medium,
    campaign: leadData.campaignName,
    utmSource: leadData.source,
    utmMedium: leadData.medium,
    utmCampaign: emailSequence.campaignName
  },
  // Metadata
  metadata: {
    isFromConversion: isFromConversion,
    originalLeadType: leadData.leadType,
    enrichmentScore: enrichmentData ? enrichmentData.enrichedScore : null,
    sequenceInfo: emailSequence.sequence || null,
    customFields: leadData.customFields || {}
  },
  createdAt: now,
  updatedAt: now
};

// ðŸŽ¯ PREPARAR EMAIL ACTIVITY
const emailActivity = {
  id: uuidv4(),
  businessId: leadData.businessId,
  type: 'email_scheduled',
  category: 'email_marketing',
  entityType: 'lead',
  entityId: leadData.id,
  leadId: leadData.id,
  contactId: null,
  title: `Email Scheduled: ${emailSequence.subject}`,
  description: `Email campaign scheduled - Campaign: ${emailSequence.campaignName} - Priority: ${emailSequence.priority}`,
  data: {
    campaignId: emailCampaignData.id,
    campaignName: emailSequence.campaignName,
    emailNumber: emailSequence.emailNumber,
    subject: emailSequence.subject,
    template: emailSequence.template,
    priority: emailSequence.priority,
    scheduledFor: emailSequence.scheduledFor,
    recipientEmail: leadData.email,
    isFromConversion: isFromConversion,
    isHotLead: leadData.isHot
  },
  metadata: {
    emailCampaign: {
      campaignName: emailSequence.campaignName,
      emailNumber: emailSequence.emailNumber,
      priority: emailSequence.priority,
      template: emailSequence.template,
      scheduledFor: emailSequence.scheduledFor
    },
    leadDetails: {
      email: leadData.email,
      name: leadData.name,
      leadType: leadData.leadType,
      isHot: leadData.isHot,
      score: enrichmentData ? enrichmentData.enrichedScore : leadData.score
    },
    attribution: emailCampaignData.attribution
  },
  source: 'email_orchestrator',
  createdAt: now
};

// ðŸŽ¯ PREPARAR SECUENCIA COMPLETA SI EXISTE
let futureEmails = [];
if (emailSequence.sequence && emailSequence.sequence.length > 1) {
  futureEmails = emailSequence.sequence.slice(1).map((emailStep, index) => {
    const scheduledDate = new Date(Date.now() + (emailStep.day * 24 * 60 * 60 * 1000));

    return {
      id: uuidv4(),
      businessId: leadData.businessId,
      leadId: leadData.id,
      contactId: null,
      campaignName: emailSequence.campaignName,
      emailNumber: index + 2, // Start from 2 since first email is #1
      subject: emailStep.subject,
      template: emailStep.template,
      status: 'pending',
      priority: 'normal',
      scheduledFor: scheduledDate.toISOString(),
      sentAt: null,
      deliveredAt: null,
      openedAt: null,
      clickedAt: null,
      recipientData: emailCampaignData.recipientData,
      attribution: emailCampaignData.attribution,
      metadata: {
        ...emailCampaignData.metadata,
        sequenceStep: index + 2,
        parentCampaignId: emailCampaignData.id
      },
      createdAt: now,
      updatedAt: now
    };
  });
}

const result = {
  success: true,
  data: {
    emailCampaign: emailCampaignData,
    emailActivity: emailActivity,
    futureEmails: futureEmails,
    sequenceInfo: {
      campaignName: emailSequence.campaignName,
      totalEmails: 1 + futureEmails.length,
      priority: emailSequence.priority,
      firstEmailScheduledFor: emailSequence.scheduledFor
    }
  },
  nextWorkflows: {
    activityLogger: true // Siempre registrar la activity
  },
  webhooks: {
    activityLogger: 'http://localhost:5678/webhook/activity-logger'
  }
};

console.log(`âœ… Email campaign orquestada`);
console.log(`ðŸ“§ Campaign: ${emailSequence.campaignName}`);
console.log(`ðŸ“¬ Subject: ${emailSequence.subject}`);
console.log(`â° Scheduled: ${emailSequence.scheduledFor}`);
console.log(`ðŸŽ¯ Priority: ${emailSequence.priority}`);
console.log(`ðŸ“Š Sequence emails: ${1 + futureEmails.length}`);

return { json: result };

// Preparar emails futuros para inserciÃ³n en lote
const payload = $input.item.json;
const futureEmails = payload.data.futureEmails || [];

if (futureEmails.length === 0) {
  return {
    json: {
      success: true,
      message: 'No future emails to schedule',
      futureEmailsScheduled: 0
    }
  };
}

// Transformar para PostgreSQL bulk insert
const insertData = futureEmails.map(email => ({
  json: {
    id: email.id,
    businessId: email.businessId,
    leadId: email.leadId,
    contactId: null,
    campaignName: email.campaignName,
    emailNumber: email.emailNumber,
    subject: email.subject,
    template: email.template,
    status: email.status,
    priority: email.priority,
    scheduledFor: email.scheduledFor,
    sentAt: null,
    deliveredAt: null,
    openedAt: null,
    clickedAt: null,
    recipientData: JSON.stringify(email.recipientData),
    attribution: JSON.stringify(email.attribution),
    metadata: JSON.stringify(email.metadata),
    createdAt: email.createdAt,
    updatedAt: email.updatedAt
  }
}));

console.log(`ðŸ“§ Programando ${insertData.length} emails futuros`);

return insertData;
